import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../componentes/Header";
import ContextoReceitas from "../contexto/Consumidor";

export default function PaginaPrincipal() {
  const { resposta, setIdIngrediente, setTipoDaPesquisa } = useContext(ContextoReceitas);
  const navigate = useNavigate();
  const [ entrada, setEntrada ] = useState({});
  return (
    <div>
      <Header />
      <form onSubmit={(e) => (e.preventDefault(), setTipoDaPesquisa(entrada), console.log('a'))}>
        <input type="text" placeholder="Pesquise seu prato" onChange={(e) => setEntrada({...entrada, valor: e.target.value })}/>
        <input type="submit" value="Pesquisar" />
        <span>Pesquisar por:</span>
        <label>
          <input type="radio" name="search" value="Prato" onChange={(e) => setEntrada({...entrada, tipo: e.target.value })}/>
          Nome do prato
        </label>
        <label>
          <input type="radio" name="search" value="Ingrediente" onChange={(e) => setEntrada({...entrada, tipo: e.target.value })}/>
          Ingrediente
        </label>
      </form>
      { resposta ? resposta.map((elemento) => {
        return (
          <card key={elemento.idMeal} className="cards" onClick={() => { navigate("/receita"); setIdIngrediente(elemento.idMeal)}}>
            <h3>{elemento.strMeal}</h3>
            <img
              src={elemento.strMealThumb}
              alt={elemento.strMeal}
              width="160"
            />
          </card>
        );
      }) : <h4> Tentamos, mas n√£o encontramos nada :( </h4>}
    </div>
  );
}
