import React, { useContext } from "react";
import { unstable_HistoryRouter } from "react-router-dom";
import Header from "../componentes/Header";
import ContextoReceitas from "../contexto/Consumidor";

export default function PaginaPrincipal() {
    const { resposta, setIdIngrediente } = useContext(ContextoReceitas);
    const history = useHistory();
    console.log(resposta);
    return (
        <div>
        <Header />
        <form onSubmit={(e) => e.preventDefault()}>
        <input
            type="text"
            placeholder="Pesquise seu prato"
        />
        <input
            type="button"
            value="Pesquisar"
        />
        </form>
         {
             resposta.map((elemento) => {
                 return (
                 <card 
                 key={ elemento.idMeal }
                 onClick={ () => (
                    history.push(`comidas/${elemento.idMeal}`,
                    setIdIngrediente(elemento.idMeal))
                 ) }>
                     <p>{elemento.strMeal}</p>
                     <img src={ elemento.strMealThumb } alt={ elemento.strMeal } width="160"/>
                 </card>
                 )
             })
         }
        </div>
    )
}
