import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../componentes/Header";
import ContextoReceitas from "../contexto/Consumidor";

export default function PaginaPrincipal() {
  const { resposta, setIdIngrediente, setTipoDaPesquisa, tipoDaPesquisa } = useContext(ContextoReceitas);
  const navigate = useNavigate();
  console.log(resposta);
  return (
    <div>
      <Header />
      <form onSubmit={(e) => e.preventDefault()}>
        <input type="text" placeholder="Pesquise seu prato" onChange={(e) => setTipoDaPesquisa({...tipoDaPesquisa, tipo: e.target.value })}/>
        <input type="button" value="Pesquisar" />
        <span>Pesquisar por:</span>
        <label>
          <input type="radio" name="search" value="Prato" onChange={(e) => setTipoDaPesquisa({...tipoDaPesquisa, tipo: e.target.value })}/>
          Nome do prato
        </label>
        <label>
          <input type="radio" name="search" value="Ingrediente" onChange={(e) => setTipoDaPesquisa({...tipoDaPesquisa, tipo: e.target.value })}/>
          Ingrediente
        </label>
      </form>
      {resposta.map((elemento) => {
        return (
          <card key={elemento.idMeal} onClick={() => navigate("/receita")}>
            <p>{elemento.strMeal}</p>
            <img
              src={elemento.strMealThumb}
              alt={elemento.strMeal}
              width="160"
            />
          </card>
        );
      })}
    </div>
  );
}
